import React, { useEffect, useState } from 'react';
import { Button, Input, Form, Col, Row, Alert } from 'antd';
import { Helmet } from 'react-helmet';
import { UserOutlined, LockOutlined, EyeInvisibleOutlined, EyeTwoTone } from '@ant-design/icons';
import './LoginPage.css';
import loginBackground from './image/bg1.jpg';
import smallBackground from './image/bg2.jpg';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Captcha from 'react-captcha-code';

const LoginPage = () => {
    const [form] = Form.useForm();
    const [captcha, setCaptcha] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const captchaConfig = {
        charNum: 4,
        width: 150,
        height: 50,
        fontSize: 24,
        expiration: 10000,
        refreshBtn: true,
        onChange: (value) => setCaptcha(value)
    };

    const onFinish = async (values) => {
        try {
            if (values.username === "admin" && values.password === "123456" && values.code === captcha) {
                sessionStorage.setItem("username", values.username);
                sessionStorage.setItem("token", "mock-token");
                navigate('/main');
            } else {
                setError('用户名、密码或验证码错误！');
            }
        } catch (error) {
            console.error('登录失败:', error);
            setError('登录请求失败，请稍后重试');
        }
    };

    const fetchCaptcha = () => {
        const newCode = Array.from({length: 4}, () =>
            Math.floor(Math.random() * 10).toString()
        ).join('');
        setCaptcha(newCode);
    };

    useEffect(() => {
        fetchCaptcha();
    }, []);

    return (
        <>
            <Helmet>
                <title>登录页</title>
            </Helmet>

            <div className="App-container">
                {/* 背景图层 */}
                <div className="left-content-container">
                    <div className="auth_bg">
                        <img id="bannerbox" src={loginBackground} alt="" style={{ width: '80%', height: 'auto', display: 'block', margin: '0 auto' }} />
                        <div style={{ position: 'absolute', top: '50%', left: '45%', transform: 'translate(-50%, -50%)' }}>
                            <img src={smallBackground} alt="" style={{ width: '700px', height: 'auto' }} />
                        </div>
                    </div>
                </div>
                {/* 登录主体容器 */}
                <div className="right-content-container">
                    <div className="auth-login-div">
                        {/* 添加标题 */}
                        <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>高校舆情查询系统</h2>
                        <Form
                            name="normal_login"
                            form={form}
                            className="login-form"
                            initialValues={{remember: true}}
                            onFinish={onFinish}
                            scrollToFirstError
                        >
                            <Form.Item
                                name="username"
                                rules={[{required: true, message: '请输入用户名'}]}
                            >
                                <Input
                                    prefix={<UserOutlined className="auth_icon auth_icon_user"/>}
                                    placeholder="用户名"
                                    className="auth_input"
                                />
                            </Form.Item>

                            <Form.Item
                                name="password"
                                rules={[{required: true, message: '请输入密码'}]}
                            >
                                <Input.Password
                                    prefix={<LockOutlined className="auth_icon auth_icon_pwd"/>}
                                    placeholder="密码"
                                    className="auth_input"
                                    iconRender={visible =>
                                        visible ? <EyeTwoTone/> : <EyeInvisibleOutlined/>
                                    }
                                    onClick={() => setShowPassword(!showPassword)}
                                />
                            </Form.Item>

                            <Form.Item
                                name="code"
                                rules={[{required: true, message: '请输入验证码'}]}
                            >
                                <Row gutter={8}>
                                    <Col span={12}>
                                        <Captcha
                                            {...captchaConfig}
                                            onChange={setCaptcha}
                                        />
                                    </Col>
                                    <Col span={12}>
                                        <Input
                                            placeholder="输入验证码"
                                            className="auth_input"
                                            onChange={(e) => setCaptcha(e.target.value)}
                                        />
                                    </Col>
                                </Row>
                            </Form.Item>

                            <Form.Item>
                                <Button
                                    type="primary"
                                    htmlType="submit"
                                    className="auth_btn"
                                    style={{
                                        backgroundColor: '#2A5CAA',
                                        borderColor: '#2A5CAA',
                                        width: '100%',
                                        height: '48px',
                                        borderRadius: '8px'
                                    }}
                                >
                                    登录
                                </Button>
                            </Form.Item>

                            {error && <Alert type="error" className="mt-4" message={error}/>}
                        </Form>
                    </div>
                </div>
            </div>
        </>
    );
};

export default LoginPage;
