import React, { useEffect, useState } from 'react';
import { Button, Input, Form, Col, Row, Alert } from 'antd';
import { Helmet } from 'react-helmet';
import { UserOutlined, LockOutlined, EyeInvisibleOutlined, EyeTwoTone } from '@ant-design/icons';
import './LoginPage.css';
import loginBackground from './image/bg1.jpg';
import smallBackground from './image/bg2.jpg';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
    const [form] = Form.useForm();
    const [backendCaptcha, setBackendCaptcha] = useState('');
    const [captchaImage, setCaptchaImage] = useState('');
    const [userInputCode, setUserInputCode] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    // 获取验证码（图像 + 代码）
    const fetchCaptchaFromBackend = async () => {
        try {
            const response = await axios.get('/api/captcha'); // 调整为你的后端接口地址
            setCaptchaImage(response.data.image);
            setBackendCaptcha(response.data.code);
        } catch (error) {
            console.error('获取验证码失败', error);
        }
    };

    useEffect(() => {
        fetchCaptchaFromBackend();
    }, []);

    const onFinish = async (values) => {
        try {
            if (values.username === "admin" && values.password === "123456" && userInputCode === backendCaptcha) {
                sessionStorage.setItem("username", values.username);
                sessionStorage.setItem("token", "mock-token");
                navigate('/main');
            } else {
                setError('用户名、密码或验证码错误！');
                fetchCaptchaFromBackend(); // 错误时刷新验证码
            }
        } catch (error) {
            console.error('登录失败:', error);
            setError('登录请求失败，请稍后重试');
        }
    };

    return (
        <>
            <Helmet>
                <title>登录页</title>
            </Helmet>

            <div className="App-container">
                {/* 背景图层 */}
                <div className="left-content-container">
                    <div className="auth_bg">
                        <img id="bannerbox" src={loginBackground} alt="" style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
                        <div style={{ position: 'absolute', top: '50%', left: '30%', transform: 'translate(-50%, -50%)' }}>
                            <img src={smallBackground} alt="" style={{ width: '700px', height: 'auto' }} />
                        </div>
                    </div>
                </div>

                {/* 登录主体容器 */}
                <div className="right-content-container" style={{ position: 'absolute', top: 0, right: 0, width: '35%', height: '100%', display: 'flex', justifyContent: 'center', paddingTop: '150px', alignItems: 'flex-start' }}>
                    <div className="auth-login-div">
                        <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>高校舆情查询系统</h2>
                        <Form
                            name="normal_login"
                            form={form}
                            className="login-form"
                            initialValues={{ remember: true }}
                            onFinish={onFinish}
                            scrollToFirstError
                        >
                            <Form.Item
                                name="username"
                                rules={[{ required: true, message: '请输入用户名' }]}
                            >
                                <Input
                                    prefix={<UserOutlined className="auth_icon auth_icon_user" />}
                                    placeholder="用户名"
                                    className="auth_input"
                                />
                            </Form.Item>

                            <Form.Item
                                name="password"
                                rules={[{ required: true, message: '请输入密码' }]}
                            >
                                <Input.Password
                                    prefix={<LockOutlined className="auth_icon auth_icon_pwd" />}
                                    placeholder="密码"
                                    className="auth_input"
                                    iconRender={visible =>
                                        visible ? <EyeTwoTone /> : <EyeInvisibleOutlined />
                                    }
                                    onClick={() => setShowPassword(!showPassword)}
                                />
                            </Form.Item>

                            <Form.Item
                                name="code"
                                rules={[{ required: true, message: '请输入验证码' }]}
                            >
                                <Row gutter={8}>
                                    <Col span={12}>
                                        <img
                                            src={captchaImage}
                                            alt="验证码"
                                            style={{ height: '50px', cursor: 'pointer' }}
                                            onClick={fetchCaptchaFromBackend}
                                        />
                                    </Col>
                                    <Col span={12}>
                                        <Input
                                            placeholder="输入验证码"
                                            className="auth_input"
                                            value={userInputCode}
                                            onChange={(e) => setUserInputCode(e.target.value)}
                                        />
                                    </Col>
                                </Row>
                            </Form.Item>

                            <Form.Item>
                                <Button
                                    type="primary"
                                    htmlType="submit"
                                    className="auth_btn"
                                    style={{
                                        backgroundColor: '#2A5CAA',
                                        borderColor: '#2A5CAA',
                                        width: '100%',
                                        height: '48px',
                                        borderRadius: '8px'
                                    }}
                                >
                                    登录
                                </Button>
                            </Form.Item>

                            {error && <Alert type="error" className="mt-4" message={error} />}
                        </Form>
                    </div>
                </div>
            </div>
        </>
    );
};

export default LoginPage;
