import React, { useEffect, useState } from 'react';
import { Button, Input, Form, Col, Row, Alert } from 'antd';
import { Helmet } from 'react-helmet';
import { UserOutlined, LockOutlined, EyeInvisibleOutlined, EyeTwoTone } from '@ant-design/icons';
import './LoginPage.css';
import loginBackground from './image/bg1.jpg';
import smallBackground from './image/bg2.jpg'; // 引入 bg2.jpg
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Captcha from 'react-captcha-code'; // 引入验证码组件

const LoginPage = () => {
    const [form] = Form.useForm();
    const [captcha, setCaptcha] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    // 验证码生成配置
    const captchaConfig = {
        charNum: 4,          // 验证码长度
        width: 150,         // 验证码宽度
        height: 50,         // 验证码高度
        fontSize: 24,        // 字体大小
        expiration: 10000,   // 验证码有效期（毫秒）
        refreshBtn: true,    // 显示刷新按钮
        onChange: (value) => setCaptcha(value)
    };

    // 表单提交处理
    const onFinish = async (values) => {
        try {
            // 这里应该调用后端接口验证
            // 示例验证逻辑（正式环境请替换为API调用）
            if (values.username === "admin" && values.password === "123456" && values.code === captcha) {
                sessionStorage.setItem("username", values.username);
                sessionStorage.setItem("token", "mock-token");
                navigate('/main');
            } else {
                setError('用户名、密码或验证码错误！');
            }
        } catch (error) {
            console.error('登录失败:', error);
            setError('登录请求失败，请稍后重试');
        }
    };

    // 获取验证码（正式环境应调用API）
    const fetchCaptcha = () => {
        // 示例：生成随机验证码（正式环境应使用服务端生成）
        const newCode = Array.from({length: 4}, () =>
            Math.floor(Math.random() * 10).toString()
        ).join('');
        setCaptcha(newCode);
    };

    useEffect(() => {
        fetchCaptcha(); // 初始化验证码
    }, []);

    return (
        <>
            <Helmet>
                <title>登录页</title>
            </Helmet>

            <div className="App-container">
                <div className="left-content-container">
                    {/* 背景图层 */}
                    <div className="auth_bg">
                        {/*  bg1.jpg */}
                        <img id="bannerbox" src={loginBackground} alt="" style={{ width: '20%', height: 'auto', display: 'block', margin: '0 auto' }} />
                        <div style={{ position: 'absolute', top: '50%', left: '40%', transform: 'translate(-50%, -50%)' }}>
                            {/* 放大 bg2.jpg */}
                            <img src={smallBackground} alt="" style={{ width: '300px', height: 'auto' }} />
                        </div>
                    </div>
                </div>
                {/* 登录主体容器 */}
                <div className="right-content-container">
                    <div className="auth_login_container">
                        <div className="auth-title-content">
                            <p className="auth-title">Login for everything</p>
                        </div>
                        <div className="auth-login-div">
                            {/* 登录表单 */}
                            <Form
                                name="normal_login"
                                form={form}
                                className="login-form"
                                initialValues={{remember: true}}
                                onFinish={onFinish}
                                scrollToFirstError
                            >
                                {/* 用户名字段 */}
                                <Form.Item
                                    name="username"
                                    rules={[{required: true, message: '请输入用户名'}]}
                                >
                                    <Input
                                        prefix={<UserOutlined className="auth_icon auth_icon_user"/>}
                                        placeholder="用户名"
                                        className="auth_input"
                                    />
                                </Form.Item>

                                {/* 密码字段 */}
                                <Form.Item
                                    name="password"
                                    rules={[{required: true, message: '请输入密码'}]}
                                >
                                    <Input.Password
                                        prefix={<LockOutlined className="auth_icon auth_icon_pwd"/>}
                                        placeholder="密码"
                                        className="auth_input"
                                        iconRender={visible =>
                                            visible ? <EyeTwoTone/> : <EyeInvisibleOutlined/>
                                        }
                                        onClick={() => setShowPassword(!showPassword)}
                                    />
                                </Form.Item>

                                {/* 验证码字段 */}
                                <Form.Item
                                    name="code"
                                    rules={[{required: true, message: '请输入验证码'}]}
                                >
                                    <Row gutter={8}>
                                        <Col span={12}>
                                            <Captcha
                                                {...captchaConfig}
                                                onChange={setCaptcha}
                                            />
                                        </Col>
                                        <Col span={12}>
                                            <Input
                                                placeholder="输入验证码"
                                                className="auth_input"
                                                // value={captcha}
                                                onChange={(e) => setCaptcha(e.target.value)}
                                            />
                                        </Col>
                                    </Row>
                                </Form.Item>

                                {/* 提交按钮 */}
                                <Form.Item>
                                    <Button
                                        type="primary"
                                        htmlType="submit"
                                        className="auth_btn"
                                        style={{
                                            backgroundColor: '#2A5CAA',
                                            borderColor: '#2A5CAA',
                                            width: '100%',
                                            height: '48px',
                                            borderRadius: '8px'
                                        }}
                                    >
                                        登录
                                    </Button>
                                </Form.Item>

                                {/* 错误提示 */}
                                {error && <Alert type="error" className="mt-4" message={error}/>}
                            </Form>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
};

export default LoginPage;
