// 引入 React 和必要的库
import React, { useEffect, useState } from 'react';
import { Helmet } from 'react-helmet';
import { Button, Card, Descriptions, Form, Input, message, Popover, Select, Space, Table, Upload } from "antd";
import Header from "../page/header";
import './gaojiSearch.css';
import axios from "axios";
import loginBackground from '../image/bg1.jpg';
import {
    InboxOutlined,
    MinusSquareOutlined,
    PlusSquareOutlined,
    QuestionCircleOutlined
} from "@ant-design/icons";
import { useLocation, useNavigate } from "react-router-dom";

const { Dragger } = Upload;

// 搜索条件相关选项
const operators = ['并且', '或者', '不包含'];
const containers = ['全部容器', '标题', '作者', '摘要'];
const fuzzyOptions = ['模糊', '精确'];

// 顶部导航组件，显示欢迎信息与退出登录按钮
function Headermain() {
    const navigate = useNavigate();

    useEffect(() => {
        // 如果未登录（无token），跳转回登录页
        if (sessionStorage.getItem('token') == null) {
            navigate("/");
        }
    }, []);

    const username = sessionStorage.getItem('username');

    const handleLogout = () => {
        // 清除登录信息
        sessionStorage.removeItem("token");
        sessionStorage.removeItem("username");
        delete axios.defaults.headers.common["Authorization"];
        navigate("/");
    };

    return (
        <div className="top-section">
            <ul className="top-section-right">
                <li> 欢迎: {username}</li>
                <button className="logout-button" onClick={handleLogout}>
                    退出登录
                </button>
            </ul>
        </div>
    );
}

// 高级搜索组件主体
function GaojiSearchComponent() {
    // 搜索条件行（每行一组组合搜索）
    const [searchRows, setSearchRows] = useState([
        { keyword1: '', container: '全部容器', operator: '并且', keyword2: '', fuzzy: '模糊' }
    ]);

    // 时间筛选
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');

    // 类型筛选
    const [searchTypes, setSearchTypes] = useState([]);
    const availableTypes = ['类型1', '类型2', '类型3'];

    // 添加一行搜索条件
    const handleAddRow = () => {
        setSearchRows([...searchRows, { keyword1: '', container: '全部容器', operator: '并且', keyword2: '', fuzzy: '模糊' }]);
    };

    // 删除指定的搜索行
    const handleRemoveRow = (index) => {
        if (searchRows.length > 1) {
            const newRows = [...searchRows];
            newRows.splice(index, 1);
            setSearchRows(newRows);
        }
    };

    // 修改搜索行中的各项值
    const handleKeyword1Change = (index, e) => {
        const newRows = [...searchRows];
        newRows[index].keyword1 = e.target.value;
        setSearchRows(newRows);
    };

    const handleOperatorChange = (index, e) => {
        const newRows = [...searchRows];
        newRows[index].operator = e.target.value;
        setSearchRows(newRows);
    };

    const handleContainerChange = (index, e) => {
        const newRows = [...searchRows];
        newRows[index].container = e.target.value;
        setSearchRows(newRows);
    };

    const handleKeyword2Change = (index, e) => {
        const newRows = [...searchRows];
        newRows[index].keyword2 = e.target.value;
        setSearchRows(newRows);
    };

    const handleFuzzyChange = (index, e) => {
        const newRows = [...searchRows];
        newRows[index].fuzzy = e.target.value;
        setSearchRows(newRows);
    };

    const handleStartDateChange = (e) => {
        setStartDate(e.target.value);
    };

    const handleEndDateChange = (e) => {
        setEndDate(e.target.value);
    };

    const handleTypeChange = (type) => {
        if (searchTypes.includes(type)) {
            setSearchTypes(searchTypes.filter((t) => t !== type));
        } else {
            setSearchTypes([...searchTypes, type]);
        }
    };

    // 点击“检索”按钮
    const handleSearch = () => {
        console.log('搜索条件:', { searchRows, startDate, endDate, searchTypes });
    };

    // 点击“清除”按钮
    const handleClear = () => {
        setSearchRows([{ keyword1: '', container: '全部容器', operator: '并且', keyword2: '', fuzzy: '模糊' }]);
        setStartDate('');
        setEndDate('');
        setSearchTypes([]);
    };

    return (
        <div className="search-page">
            <h2>高级检索</h2>
            <div className="main-content">
                <div className="search-content">
                    {/* 多行搜索条件输入区域 */}
                    <div className="search-rows">
                        {searchRows.map((row, index) => (
                            <div className="search-row" key={index}>
                                <div className="search-item">
                                    <select value={row.container} onChange={(e) => handleContainerChange(index, e)} className="select-box">
                                        {containers.map((container) => (
                                            <option key={container} value={container}>{container}</option>
                                        ))}
                                    </select>
                                </div>
                                <div className="search-item">
                                    <input type="text" value={row.keyword1} onChange={(e) => handleKeyword1Change(index, e)} className="input-box" />
                                </div>
                                <div className="search-item">
                                    <select value={row.operator} onChange={(e) => handleOperatorChange(index, e)} className="select-box">
                                        {operators.map((operator) => (
                                            <option key={operator} value={operator}>{operator}</option>
                                        ))}
                                    </select>
                                </div>
                                <div className="search-item">
                                    <input type="text" value={row.keyword2} onChange={(e) => handleKeyword2Change(index, e)} className="input-box" />
                                </div>
                                <div className="search-item">
                                    <select value={row.fuzzy} onChange={(e) => handleFuzzyChange(index, e)} className="select-box">
                                        {fuzzyOptions.map((option) => (
                                            <option key={option} value={option}>{option}</option>
                                        ))}
                                    </select>
                                </div>
                                <div className="search-item">
                                    <button onClick={() => handleAddRow()} className="action-button">+</button>
                                    <button onClick={() => handleRemoveRow(index)} className="action-button">-</button>
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* 时间范围输入 */}
                    <div className="date-section">
                        <label>日期:</label>
                        <input type="date" value={startDate} onChange={handleStartDateChange} className="input-box" />
                        <span>至:</span>
                        <input type="date" value={endDate} onChange={handleEndDateChange} className="input-box" />
                    </div>

                    {/* 类型选择复选框 */}
                    <div className="type-section">
                        <label>类型:</label>
                        <div className="type-options">
                            {availableTypes.map((type) => (
                                <div className="type-item" key={type}>
                                    <input
                                        type="checkbox"
                                        checked={searchTypes.includes(type)}
                                        onChange={() => handleTypeChange(type)}
                                    />
                                    {type}
                                </div>
                            ))}
                        </div>
                    </div>

                    {/* 操作按钮区 */}
                    <div className="button-section">
                        <button onClick={handleClear} className="action-button">清除所有检索选项</button>
                        <button onClick={handleSearch} className="action-button">检索</button>
                    </div>
                </div>

                {/* 检索说明 */}
                <div className="search-instruction">
                    <h3>检索说明区</h3>
                    <p>这里可以添加检索的相关说明信息。</p>
                </div>
            </div>
        </div>
    );
}

